// Filter for blogs needing LinkedIn promotion
const blogs = $input.all();
const needsPromo = [];

for (const blog of blogs) {
  const json = blog.json;

  // Check if it needs promo and hasn't been generated yet
  const willGeneratePromo =
    json.properties?.["Will Generate LinkedIn Promo"]?.checkbox === true;
  const alreadyGenerated =
    json.properties?.["LinkedIn Promo Generated"]?.checkbox === true;
  const publishedDate = json.properties?.["Publish Date"]?.date?.start
    ? new Date(json.properties["Publish Date"].date.start)
    : null;

  // Only generate promo for posts published in last 7 days
  const daysSincePublished = publishedDate
    ? (Date.now() - publishedDate.getTime()) / (1000 * 60 * 60 * 24)
    : 999;

  if (willGeneratePromo && !alreadyGenerated && daysSincePublished <= 7) {
    needsPromo.push({
      json: {
        notion_page_id: json.id,
        blog_title:
          json.properties?.Title?.title?.[0]?.plain_text || "Untitled",
        blog_slug: json.properties?.slug?.rich_text?.[0]?.plain_text || "",
        blog_excerpt:
          json.properties?.Description?.rich_text?.[0]?.plain_text || "",
        content_pillar: json.properties?.["Content Pillar"]?.select?.name || "",
        blog_url: `https://xavierfuentes.com/${
          json.properties?.slug?.rich_text?.[0]?.plain_text || ""
        }`,
        published_date: json.properties?.["Publish Date"]?.date?.start,
        days_since_published: Math.round(daysSincePublished),
      },
    });
  }
}

console.log(`Found ${needsPromo.length} blog posts needing LinkedIn promotion`);

return needsPromo.length > 0 ? needsPromo : [];

// Prepare LinkedIn promotion file for GitHub
const item = $input.item.json;

// Extract generated content from OpenAI response
const linkedinContent = item.message?.content || item.text || "";

// Create slug for LinkedIn promo
const slug = `${item.blog_slug}-linkedin-promo`;

// Generate frontmatter and content
const markdown = `---
  title: "New: ${item.blog_title}"
  slug: ${slug}
  status: draft
  target_channel: linkedin-promotion
  content_pillar: ${item.content_pillar}
  blog_post_slug: ${item.blog_slug}
  blog_post_url: ${item.blog_url}
  linkedin_day: various
  autogenerated: true
  generated_date: ${new Date().toISOString().split("T")[0]}
  ---

  ${linkedinContent}

  ---

  *Auto-generated LinkedIn promotion for blog post: ${item.blog_title}*
  *Read the full article: ${item.blog_url}*`;

// Generate filename with date prefix
const now = new Date();
const datePrefix = now.toISOString().split("T")[0]; // YYYY-MM-DD format
const filename = `${datePrefix}-${slug}.md`;

return {
  json: {
    notion_page_id: item.notion_page_id,
    blog_slug: item.blog_slug,
    blog_title: item.blog_title,
    blog_url: item.blog_url,
    markdown_content: markdown,
    filename: filename,
    file_path: `content/linkedin/${filename}`,
    commit_message: `feat: add LinkedIn promotion for "${item.blog_title}"`,
  },
};

// Extract blog content (first 1000 chars for context)
const item = $input.item.json;
const fullContent = item.content || '';

// Remove frontmatter
const contentWithoutFrontmatter = fullContent.replace(/^---[\s\S]*?---\n/, '');

// Get first 1000 characters for context
const contentSummary = contentWithoutFrontmatter.substring(0, 1000);

return {
  json: {
    ...item,
    blog_content_summary: contentSummary,
    blog_full_length: fullContent.length
  }
};
