// Enhanced research analysis and prompt preparation
// Leverages all available metadata for richer content generation

const items = $input.all();
const enhancedItems = [];

for (const item of items) {
  const json = item.json;

  // Extract and validate core properties from Notion data
  const title = json.property_title || json.name || "Untitled";
  const description = json.property_description || "No description available";
  const contentPillar = json.property_content_pillar || "Technology Strategy";
  const priority = json.property_priority || "Medium";
  const targetAudience = json.property_target_audience || ["Tech Leaders"];
  const seoKeywords = json.property_seo_keywords || "";
  const source = json.property_source || "Unknown";
  const articleLink = json.property_article_link || "";
  const notes = json.property_notes || "";

  // Quality scores from Notion
  const relevanceScore = json.property_relevance_score || 5;
  const actionabilityScore = json.property_actionability_score || 5;
  const depthScore = json.property_depth_score || 5;
  const wordCountTarget = json.property_word_count_target || 1500;

  // Calculate content strategy alignment
  const pillarWeights = {
    "Technology Strategy": 0.3,
    "Leadership & Management": 0.25,
    "Execution & Delivery": 0.2,
    "Founder Lessons": 0.15,
    "Market & Trends": 0.1,
  };

  const strategicWeight = pillarWeights[contentPillar] || 0.1;
  const qualityScore =
    (relevanceScore * 3 + actionabilityScore * 2 + depthScore) / 6;

  // Content type recommendation based on scores and audience
  let contentType = "analysis";
  if (actionabilityScore >= 7) contentType = "practical guide";
  if (depthScore >= 8) contentType = "deep dive";
  if (contentPillar === "Founder Lessons") contentType = "case study";

  // Audience-specific angle suggestions
  const audienceAngles = {
    CTOs: "strategic decision-making and technical leadership perspective",
    "Engineering Managers": "team management and technical execution focus",
    "Tech Leaders": "leadership and technical strategy balance",
    "Product Managers": "product strategy and cross-functional collaboration",
    Founders: "business strategy and scalability implications",
    "VCs & Investors": "market opportunity and competitive advantage analysis",
  };

  const primaryAudience = Array.isArray(targetAudience)
    ? targetAudience[0]
    : "Tech Leaders";
  const audienceAngle =
    audienceAngles[primaryAudience] || "practical leadership insights";

  // SEO keyword enhancement
  const enhancedKeywords = seoKeywords
    ? seoKeywords
        .split(",")
        .map((k) => k.trim())
        .filter((k) => k.length > 0)
    : [
        "technology leadership",
        contentPillar.toLowerCase().replace(" & ", " "),
      ];

  // Content structure recommendation
  const structureMap = {
    "practical guide":
      "Problem ‚Üí Solution Framework ‚Üí Step-by-Step Implementation ‚Üí Common Pitfalls ‚Üí Next Actions",
    "deep dive":
      "Context ‚Üí Technical Analysis ‚Üí Case Studies ‚Üí Strategic Implications ‚Üí Future Outlook",
    "case study":
      "Challenge ‚Üí Decision Process ‚Üí Implementation ‚Üí Results ‚Üí Lessons Learned",
    analysis:
      "Current State ‚Üí Key Insights ‚Üí Strategic Framework ‚Üí Practical Applications ‚Üí Recommendations",
  };

  const recommendedStructure =
    structureMap[contentType] || structureMap["analysis"];

  enhancedItems.push({
    ...item,
    json: {
      ...json,
      // Enhanced metadata using actual Notion data
      enhanced_title: title,
      enhanced_description: description,
      content_pillar: contentPillar,
      priority_level: priority,
      target_audience: targetAudience,
      primary_audience: primaryAudience,
      audience_angle: audienceAngle,
      seo_keywords: enhancedKeywords,
      source_name: source,
      article_url: articleLink,
      research_notes: notes,

      // Quality metrics from Notion
      relevance_score: relevanceScore,
      actionability_score: actionabilityScore,
      depth_score: depthScore,
      quality_score: qualityScore,
      strategic_weight: strategicWeight,
      word_count_target: wordCountTarget,

      // Content strategy
      content_type: contentType,
      recommended_structure: recommendedStructure,

      // Processing metadata
      processing_timestamp: new Date().toISOString(),
      content_id: json.id,
      retry_count: 0,
    },
  });
}

console.log(
  `Enhanced ${enhancedItems.length} research items for draft generation`
);
return enhancedItems;

// Quality validation and content analysis
// Validates generated content meets quality standards

const generatedContent = $input.item.json;
const researchData = $("Enhance Research Data").item.json;
const contentText = generatedContent.message?.content || "";

// Content quality checks
const wordCount = contentText
  .split(/\s+/)
  .filter((word) => word.length > 0).length;
const targetWordCount = researchData.word_count_target || 1500;
const wordCountVariance =
  Math.abs(wordCount - targetWordCount) / targetWordCount;

// Structure validation
const hasHeadings = /#{1,3}\s/.test(contentText);
const hasSections = contentText.split("\n\n").length >= 5;
const hasConclusion = /(?:conclusion|next steps|summary|takeaway)/i.test(
  contentText
);

// Content quality indicators
const hasSpecificExamples =
  /\d+[%$¬£‚Ç¨]|\d+\s*(?:users|customers|employees|teams)/i.test(contentText);
const hasActionableAdvice =
  /(?:step|action|implement|process|method|framework)/i.test(contentText);
const hasUKSpelling = /(?:colour|optimise|realise|whilst)/i.test(contentText);

// SEO keyword integration check
const seoKeywords = researchData.seo_keywords || [];
const keywordIntegration =
  seoKeywords.filter((keyword) => new RegExp(keyword, "i").test(contentText))
    .length / Math.max(seoKeywords.length, 1);

// Calculate quality score
let qualityScore = 0;
if (wordCountVariance < 0.2) qualityScore += 20; // Word count within 20%
if (hasHeadings) qualityScore += 15;
if (hasSections) qualityScore += 15;
if (hasConclusion) qualityScore += 10;
if (hasSpecificExamples) qualityScore += 15;
if (hasActionableAdvice) qualityScore += 15;
if (hasUKSpelling) qualityScore += 5;
if (keywordIntegration >= 0.5) qualityScore += 5;

// Quality assessment
let qualityLevel = "Poor";
if (qualityScore >= 80) qualityLevel = "Excellent";
else if (qualityScore >= 65) qualityLevel = "Good";
else if (qualityScore >= 50) qualityLevel = "Acceptable";

// Generate content title with AI prefix
const originalTitle = researchData.enhanced_title;
const contentPillar = researchData.content_pillar;
const aiDraftTitle = `AI Draft: ${originalTitle}`;

// Create quality report
const qualityReport = {
  overall_score: qualityScore,
  quality_level: qualityLevel,
  word_count: wordCount,
  target_word_count: targetWordCount,
  word_count_variance: Math.round(wordCountVariance * 100),
  structure_score:
    (hasHeadings ? 15 : 0) + (hasSections ? 15 : 0) + (hasConclusion ? 10 : 0),
  content_score:
    (hasSpecificExamples ? 15 : 0) + (hasActionableAdvice ? 15 : 0),
  seo_integration: Math.round(keywordIntegration * 100),
  uk_spelling_check: hasUKSpelling,
  validation_timestamp: new Date().toISOString(),
};

console.log(`Content Quality Assessment:`);
console.log(`  Overall Score: ${qualityScore}/100 (${qualityLevel})`);
console.log(
  `  Word Count: ${wordCount}/${targetWordCount} (${Math.round(
    wordCountVariance * 100
  )}% variance)`
);
console.log(`  SEO Integration: ${Math.round(keywordIntegration * 100)}%`);
console.log(`  UK Spelling: ${hasUKSpelling ? "Present" : "Missing"}`);

return {
  json: {
    // Original research data
    ...researchData,

    // Generated content
    generated_content: contentText,
    draft_title: aiDraftTitle,

    // Quality validation
    quality_report: qualityReport,

    // Processing metadata
    generation_timestamp: new Date().toISOString(),
    needs_manual_review: qualityScore < 65,
  },
};

// Generate comprehensive email notification
// Provides detailed summary of draft generation results

const contentData = $('Validate Content Quality').item.json;
const qualityReport = contentData.quality_report;

// Build email content
let emailBody = `<h2>üìù AI Draft Generated - ${contentData.draft_title}</h2>\n\n`;

// Quality summary with visual indicators
let qualityEmoji = 'üî¥';
if (qualityReport.overall_score >= 80) qualityEmoji = 'üü¢';
else if (qualityReport.overall_score >= 65) qualityEmoji = 'üü°';

emailBody += `<h3>${qualityEmoji} Quality Assessment</h3>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li><strong>Overall Score:</strong> ${qualityReport.overall_score}/100 (${qualityReport.quality_level})</li>\n`;
emailBody += `  <li><strong>Word Count:</strong> ${qualityReport.word_count}/${qualityReport.target_word_count} (${qualityReport.word_count_variance}% variance)</li>\n`;
emailBody += `  <li><strong>SEO Integration:</strong> ${qualityReport.seo_integration}% of keywords included</li>\n`;
emailBody += `  <li><strong>UK Spelling:</strong> ${qualityReport.uk_spelling_check ? '‚úÖ Present' : '‚ùå Missing'}</li>\n`;
emailBody += `  <li><strong>Manual Review:</strong> ${contentData.needs_manual_review ? '‚ö†Ô∏è Recommended' : '‚úÖ Optional'}</li>\n`;
emailBody += `</ul>\n\n`;

// Content details
emailBody += `<h3>üìä Content Details</h3>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li><strong>Content Pillar:</strong> ${contentData.content_pillar}</li>\n`;
emailBody += `  <li><strong>Target Audience:</strong> ${contentData.target_audience.join(', ')}</li>\n`;
emailBody += `  <li><strong>Content Type:</strong> ${contentData.content_type}</li>\n`;
emailBody += `  <li><strong>Source:</strong> ${contentData.source_name}</li>\n`;
emailBody += `</ul>\n\n`;

// Quality breakdown
emailBody += `<h3>üîç Quality Breakdown</h3>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li><strong>Structure Score:</strong> ${qualityReport.structure_score}/40</li>\n`;
emailBody += `  <li><strong>Content Score:</strong> ${qualityReport.content_score}/30</li>\n`;
emailBody += `  <li><strong>Technical Score:</strong> ${qualityReport.overall_score - qualityReport.structure_score - qualityReport.content_score}/30</li>\n`;
emailBody += `</ul>\n\n`;

// Next steps
emailBody += `<h3>üöÄ Next Steps</h3>\n`;
if (contentData.needs_manual_review) {
  emailBody += `<p><strong>‚ö†Ô∏è Manual Review Required</strong></p>\n`;
  emailBody += `<ul>\n`;
  emailBody += `  <li>Review content for accuracy and brand voice</li>\n`;
  emailBody += `  <li>Check factual claims and examples</li>\n`;
  emailBody += `  <li>Enhance SEO keyword integration if needed</li>\n`;
  emailBody += `  <li>Verify UK English spelling throughout</li>\n`;
  emailBody += `</ul>\n`;
} else {
  emailBody += `<p><strong>‚úÖ Ready for Publication Review</strong></p>\n`;
  emailBody += `<ul>\n`;
  emailBody += `  <li>Content meets quality standards</li>\n`;
  emailBody += `  <li>Review for final editorial polish</li>\n`;
  emailBody += `  <li>Schedule for publication</li>\n`;
  emailBody += `</ul>\n`;
}

// Links
emailBody += `<h3>üîó Quick Links</h3>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li><a href="https://www.notion.so/${contentData.content_id.replace(/-/g, '')}">Original Research Item</a></li>\n`;
emailBody += `  <li><a href="https://www.notion.so/0d7b48da0a604c348fecbb1617b8c8a6">Content Assets Database</a></li>\n`;
emailBody += `  <li><a href="https://www.notion.so/9d899a561818444f8de1d7bd72ee1528">Content Pipeline</a></li>\n`;
emailBody += `</ul>\n\n`;

// Footer
emailBody += `<hr>\n`;
emailBody += `<p><small>Generated at ${new Date().toLocaleString('en-GB', { timeZone: 'Europe/London' })}</small></p>`;

// Determine subject line urgency
let subjectPrefix = 'üìù';
if (contentData.needs_manual_review) subjectPrefix = '‚ö†Ô∏è';
else if (qualityReport.overall_score >= 80) subjectPrefix = 'üü¢';

return [{
  json: {
    subject: `${subjectPrefix} AI Draft Ready: ${contentData.enhanced_title}`,
    body: emailBody,
    quality_score: qualityReport.overall_score,
    needs_review: contentData.needs_manual_review,
    content_title: contentData.enhanced_title
  }
}];

// Aggregate all processing results into summary
// Collects data from all processed items for final report

// Note: This runs after the loop completes, so we need to look at all previous executions
const allItems = [];
const validationNodes = $('Validate Content Quality').all();

let successCount = 0;
let totalProcessed = 0;
let needsReviewCount = 0;
const processedItems = [];

for (const item of validationNodes) {
  totalProcessed++;
  const data = item.json;

  if (data.quality_report) {
    successCount++;

    if (data.needs_manual_review) {
      needsReviewCount++;
    }

    processedItems.push({
      title: data.draft_title,
      quality_score: data.quality_report.overall_score,
      quality_level: data.quality_report.quality_level,
      word_count: data.quality_report.word_count,
      needs_review: data.needs_manual_review,
      content_id: data.content_id,
      pillar: data.content_pillar
    });
  }
}

const errorCount = totalProcessed - successCount;

// Build summary email
let emailBody = `<h2>üìä Research to Draft Workflow Complete</h2>\n\n`;

emailBody += `<h3>Summary</h3>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li><strong>Total Items:</strong> ${totalProcessed}</li>\n`;
emailBody += `  <li><strong>‚úÖ Successful:</strong> ${successCount}</li>\n`;
emailBody += `  <li><strong>‚ùå Failed:</strong> ${errorCount}</li>\n`;
emailBody += `  <li><strong>‚ö†Ô∏è Needs Review:</strong> ${needsReviewCount}</li>\n`;
emailBody += `</ul>\n\n`;

if (processedItems.length > 0) {
  emailBody += `<h3>üìù Generated Drafts</h3>\n`;
  emailBody += `<table style="border-collapse: collapse; width: 100%;">\n`;
  emailBody += `  <tr style="background-color: #f0f0f0;">\n`;
  emailBody += `    <th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Title</th>\n`;
  emailBody += `    <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Quality</th>\n`;
  emailBody += `    <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Words</th>\n`;
  emailBody += `    <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Status</th>\n`;
  emailBody += `  </tr>\n`;

  for (const item of processedItems) {
    const reviewBadge = item.needs_review ? '‚ö†Ô∏è Review' : '‚úÖ Ready';
    const qualityColor = item.quality_score >= 80 ? '#4CAF50' : item.quality_score >= 65 ? '#FF9800' : '#F44336';

    emailBody += `  <tr>\n`;
    emailBody += `    <td style="border: 1px solid #ddd; padding: 8px;">${item.title}</td>\n`;
    emailBody += `    <td style="border: 1px solid #ddd; padding: 8px; text-align: center; background-color: ${qualityColor}; color: white; font-weight: bold;">${item.quality_score}</td>\n`;
    emailBody += `    <td style="border: 1px solid #ddd; padding: 8px; text-align: center;">${item.word_count}</td>\n`;
    emailBody += `    <td style="border: 1px solid #ddd; padding: 8px; text-align: center;">${reviewBadge}</td>\n`;
    emailBody += `  </tr>\n`;
  }

  emailBody += `</table>\n\n`;
}

emailBody += `<h3>üîó Quick Links</h3>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li><a href="https://www.notion.so/0d7b48da0a604c348fecbb1617b8c8a6">Content Assets Database</a></li>\n`;
emailBody += `  <li><a href="https://www.notion.so/9d899a561818444f8de1d7bd72ee1528">Content Pipeline</a></li>\n`;
emailBody += `</ul>\n\n`;

emailBody += `<hr>\n`;
emailBody += `<p><small>Completed at ${new Date().toLocaleString('en-GB', { timeZone: 'Europe/London' })}</small></p>`;

let subjectPrefix = '‚úÖ';
if (errorCount > 0) subjectPrefix = '‚ö†Ô∏è';
else if (needsReviewCount > 0) subjectPrefix = 'üìù';

return [{
  json: {
    subject: `${subjectPrefix} Draft Generation Complete: ${successCount}/${totalProcessed} successful`,
    body: emailBody,
    total_processed: totalProcessed,
    success_count: successCount,
    error_count: errorCount,
    needs_review_count: needsReviewCount
  }
}];

// Increment retry counter to prevent infinite loops
// Tracks how many times we've attempted to improve this draft

const currentData = $input.item.json;
const currentRetryCount = currentData.retry_count || 0;
const newRetryCount = currentRetryCount + 1;

console.log(
  `Retry attempt ${newRetryCount}/3 for: ${currentData.enhanced_title}`
);

return {
  json: {
    ...currentData,
    retry_count: newRetryCount,
    last_retry_timestamp: new Date().toISOString(),
  },
};

// Merge reviewer feedback into data for regeneration
// Injects specific improvement instructions into the prompt data

const currentData = $input.item.json;
const reviewerFeedback =
  currentData.message?.content || "No specific feedback provided";

console.log(`Adding reviewer feedback for retry attempt`);
console.log(`Feedback length: ${reviewerFeedback.length} characters`);

return {
  json: {
    ...currentData,
    // Add reviewer feedback to be used in regeneration prompt
    reviewer_feedback: reviewerFeedback,

    // Keep quality report from previous attempt for comparison
    previous_quality_score: currentData.quality_report?.overall_score || 0,
    previous_attempt_content: currentData.generated_content,

    // Clear the old generated content so AI starts fresh
    generated_content: null,

    // Preserve all other metadata
    feedback_timestamp: new Date().toISOString(),
  },
};

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();
