// Validates RSS feed URLs from Notion database and checks if sources are stale
// Adds metadata for downstream processing and filters invalid sources
const out = [];

for (const it of $input.all()) {
  // Your data has property_url directly, not nested in properties
  const url = it.json.property_url; // Changed this line
  const sourceName = it.json.property_source_name || it.json.name || "Unknown"; // Multiple fallbacks
  const lastScraped = it.json.property_last_scraped?.start;

  console.log(`Processing source: ${sourceName}, URL: ${url}`);

  // Calculate staleness
  let isStale = false;
  if (lastScraped) {
    const days = (Date.now() - new Date(lastScraped).getTime()) / 86400000;
    isStale = days > 7;
  }

  // Add feed URL for downstream processing
  it.json.feedUrl = url;
  it.json.sourceName = sourceName;
  it.json.isStale = isStale;

  // Add validation status
  it.json.isValidUrl = !!(url && /^https?:\/\//.test(url));
  it.json.validationError = null;

  // Add priority multiplier
  it.json.priorityMultiplier = it.json.property_priority_multiplier || 1.0;

  // Add content quality level
  it.json.sourceContentQuality = it.json.property_content_quality || "Unknown";

  if (!url) {
    it.json.validationError = "No URL provided";
    console.log(`❌ ${sourceName}: No URL provided`);
  } else if (!/^https?:\/\//.test(url)) {
    it.json.validationError = "Invalid URL format";
    console.log(`❌ ${sourceName}: Invalid URL format: ${url}`);
  } else {
    console.log(`✅ ${sourceName}: Valid URL: ${url}`);
  }

  // ALWAYS add to output
  out.push(it);
}

console.log(
  `Validated ${out.length} sources (${
    out.filter((s) => s.json.isValidUrl).length
  } valid, ${out.filter((s) => !s.json.isValidUrl).length} invalid)`
);

return out;

