  "Leadership & Management",
  "Technology Strategy",
  "Execution & Delivery",
  "Founder Lessons",
  "Market & Trends",
  "Personal Angle",
];
const normPillar = (p) => {
  if (typeof p === "string") {
    const exact = validPillars.find((v) => v === p.trim());
    if (exact) return exact;
    const lower = p.toLowerCase();
    const fuzzy = validPillars.find(
      (v) => v.toLowerCase().includes(lower) || lower.includes(v.toLowerCase())
    );
    if (fuzzy) return fuzzy;
  }
  return "Market & Trends";
};
const normPriority = (p) => {
  const s = String(p || "Medium").toLowerCase();
  return s === "high" ? "High" : s === "low" ? "Low" : "Medium";
};
const clamp10 = (v, d = 5) => {
  const n = Number(v);
  const i = Number.isFinite(n) ? Math.round(n) : d;
  return Math.max(1, Math.min(10, i));
};
const normalizeKeywords = (k) =>
  String(k || "")
    .split(",")
    .map((s) => s.trim())
    .filter(Boolean)
    .slice(0, 8)
    .join(", ")
