    const s = cleaned.indexOf("["),
      e = cleaned.lastIndexOf("]");
    if (s !== -1 && e !== -1) cleaned = cleaned.slice(s, e + 1);
    const parsed = JSON.parse(cleaned);
    return Array.isArray(parsed) ? parsed : [parsed];
  } catch {
