// Helper function to analyse different error types
function analyzeError(httpResponse) {
  const data = httpResponse?.data || "";
  const statusCode = httpResponse?.statusCode;

  if (statusCode === 401) {
    return {
      type: "AUTHENTICATION_REQUIRED",
      statusCode: 401,
      message: "Authentication required - API key needed",
      needsApiKey: true,
    };
  }

  if (statusCode === 403) {
    return {
      type: "ACCESS_FORBIDDEN",
      statusCode: 403,
      message: "Access forbidden - may need API key",
      needsApiKey: true,
    };
  }

  if (statusCode === 404) {
    return {
      type: "FEED_NOT_FOUND",
      statusCode: 404,
      message: "RSS feed not found - URL may have changed",
      needsApiKey: false,
    };
  }

  if (!data || data.trim() === "") {
    return {
      type: "EMPTY_RESPONSE",
      statusCode: statusCode || 200,
      message: "Empty response received",
      needsApiKey: false,
    };
  }

  return {
    type: "UNKNOWN_ERROR",
    statusCode: statusCode || 0,
    message: "Unknown error occurred",
    needsApiKey: false,
  };
}

return items;

// Automatically deactivates RSS sources after multiple consecutive failures
// Updates Notion database with failure tracking and API key requirements

const items = [];

try {
  // Get all items from error handling
  const allItems = $input.all();
  const staticData = $getWorkflowStaticData("global");
  const sourceErrors = staticData.sourceErrors || {};

  // Track sources that need updates
  const sourcesToUpdate = [];
  const sourcesToFlag = [];

  for (const item of allItems) {
    const data = item.json;

    // Skip non-error items
    if (!data.failed) {
      items.push(item);
      continue;
    }

    const sourceId = data.sourceId;
    const errorHistory = sourceErrors[`source_${sourceId}`];

    if (!errorHistory) {
      items.push(item);
      continue;
    }

    // Determine what updates are needed
    const updates = {
      sourceId,
      sourceName: data.sourceName,
      updates: [],
    };

    // Check if source should be deactivated
    if (
      errorHistory.shouldDeactivate &&
      errorHistory.consecutiveFailures >= 3
    ) {
      updates.updates.push({
        field: "Active",
        value: false,
        reason: `Auto-deactivated after ${errorHistory.consecutiveFailures} consecutive failures`,
      });
    }

    // Check if API key is needed
    if (errorHistory.needsApiKey) {
      updates.updates.push({
        field: "API Key Required",
        value: true,
        reason: "Source requires authentication or subscription",
      });
    }

    // Add failure count to notes
    const errorSummary =
      `Last failed: ${new Date().toLocaleString('en-GB', { timeZone: 'Europe/London' })}. ` +
      `Consecutive failures: ${errorHistory.consecutiveFailures}. ` +
      `Error type: ${data.errorType}. ` +
      `Message: ${data.errorMessage}`;

    updates.updates.push({
      field: "Notes",
      value: errorSummary,
      reason: "Error tracking information",
    });

    if (updates.updates.length > 0) {
      sourcesToUpdate.push(updates);
    }

    // Create output item for the source update
    const updateItem = {
      json: {
        // Notion page identification
        id: sourceId,
        sourceName: data.sourceName,

        // Status updates
        shouldDeactivate: errorHistory.shouldDeactivate,
        needsApiKey: errorHistory.needsApiKey,
        consecutiveFailures: errorHistory.consecutiveFailures,

        // Update data for Notion
        updateActive: errorHistory.shouldDeactivate ? false : true,
        updateApiKeyRequired: errorHistory.needsApiKey ? true : false,
        updateNotes: errorSummary,
        updateLastErrorDate: new Date().toISOString(),

        // Original error data
        errorType: data.errorType,
        errorMessage: data.errorMessage,
        failedAt: data.failedAt,

        // Control flags
        needsNotionUpdate: true,
        isErrorHandling: true,
      },
    };

    items.push(updateItem);
  }

  // Log summary of actions
  console.log(`Source Management Summary:`);
  console.log(`- Total items processed: ${allItems.length}`);
  console.log(`- Sources needing updates: ${sourcesToUpdate.length}`);

  sourcesToUpdate.forEach((source) => {
    console.log(`Source: ${source.sourceName}`);
    source.updates.forEach((update) => {
      console.log(`  - ${update.field}: ${update.value} (${update.reason})`);
    });
  });

  // If no items need updates, create a summary item
  if (items.length === 0) {
    items.push({
      json: {
        summary: "No source updates needed",
        processedAt: new Date().toISOString(),
        totalItemsChecked: allItems.length,
        needsNotionUpdate: false,
      },
    });
  }
} catch (error) {
  console.error("Error in auto-deactivate sources:", error);

  items.push({
    json: {
      error: true,
      errorMessage: error.message,
      errorType: "AUTO_DEACTIVATE_ERROR",
      processedAt: new Date().toISOString(),
      needsNotionUpdate: false,
    },
  });
}

return items;
