// Auto-Archive Engine - Manages research queue lifecycle
// Archives stale, low-scoring, or excess articles to keep queue fresh

const articles = $input.all();
const now = new Date();
const ONE_DAY = 24 * 60 * 60 * 1000;

// Archive criteria
const MAX_QUEUE_SIZE = 60; // Keep only top 60 articles
const AUTO_ARCHIVE_DAYS = 45; // Archive anything older than 45 days
const STALE_THRESHOLD_DAYS = 30; // Archive if older than 30 days AND low score
const LOW_SCORE_THRESHOLD = 15; // Combined with age for archiving
const VERY_LOW_SCORE_THRESHOLD = 12; // Archive immediately regardless of age

const toArchive = [];
const toKeep = [];

// First pass: identify articles to archive based on quality/age
articles.forEach(article => {
  const json = article.json;

  // Calculate age
  const createdDate = new Date(json.property_created_date || json.created_date || now);
  const daysSinceCreated = (now - createdDate) / ONE_DAY;

  // Calculate basic score for archiving decisions
  const relevance = json.property_relevance_score || 5;
  const actionability = json.property_actionability_score || 5;
  const depth = json.property_depth_score || 5;
  const priority = json.property_priority || 'Medium';

  const priorityBonus = { 'High': 4, 'Medium': 2, 'Low': 0 }[priority] || 2;
  const basicScore = (relevance * 3) + (actionability * 2) + depth + priorityBonus;

  // Archive decision logic
  const shouldArchive = 
    // Very low quality - archive immediately
    basicScore < VERY_LOW_SCORE_THRESHOLD ||
    // Very old - archive regardless of score
    daysSinceCreated > AUTO_ARCHIVE_DAYS ||
    // Medium age + low score - archive gradually
    (daysSinceCreated > STALE_THRESHOLD_DAYS && basicScore < LOW_SCORE_THRESHOLD);

  if (shouldArchive) {
    toArchive.push({
      ...article,
      json: {
        ...json,
        archive_reason: getArchiveReason(basicScore, daysSinceCreated),
        archived_at: now.toISOString(),
        final_score: basicScore
      }
    });
  } else {
    toKeep.push({
      ...article,
      json: {
        ...json,
        days_in_queue: Math.round(daysSinceCreated),
        basic_score: basicScore
      }
    });
  }
});

// Second pass: if queue is still too large, archive lowest scoring
if (toKeep.length > MAX_QUEUE_SIZE) {
  // Sort by basic score (lowest first) and archive the excess
  toKeep.sort((a, b) => a.json.basic_score - b.json.basic_score);

  const excess = toKeep.splice(0, toKeep.length - MAX_QUEUE_SIZE);
  excess.forEach(article => {
    toArchive.push({
      ...article,
      json: {
        ...article.json,
        archive_reason: 'Queue size limit - lowest scoring',
        archived_at: now.toISOString(),
        final_score: article.json.basic_score
      }
    });
  });
}

// Helper function to determine archive reason
function getArchiveReason(score, days) {
  if (score < VERY_LOW_SCORE_THRESHOLD) return `Very low quality (score: ${score})`;
  if (days > AUTO_ARCHIVE_DAYS) return `Too old (${Math.round(days)} days)`;
  if (days > STALE_THRESHOLD_DAYS && score < LOW_SCORE_THRESHOLD) {
    return `Stale and low quality (${Math.round(days)} days, score: ${score})`;
  }
  return 'Queue management';
}

// Summary statistics
const archiveSummary = {
  total_reviewed: articles.length,
  archived_count: toArchive.length,
  kept_count: toKeep.length,
  archive_reasons: {},
  avg_archived_score: 0,
  avg_kept_score: 0
};

// Calculate archive reason distribution
toArchive.forEach(article => {
  const reason = article.json.archive_reason.split(' (')[0]; // Get main reason
  archiveSummary.archive_reasons[reason] = (archiveSummary.archive_reasons[reason] || 0) + 1;
});

// Calculate average scores
if (toArchive.length > 0) {
  archiveSummary.avg_archived_score = Math.round(
    (toArchive.reduce((sum, a) => sum + a.json.final_score, 0) / toArchive.length) * 10
  ) / 10;
}

if (toKeep.length > 0) {
  archiveSummary.avg_kept_score = Math.round(
    (toKeep.reduce((sum, a) => sum + a.json.basic_score, 0) / toKeep.length) * 10
  ) / 10;
}

console.log('🗄️ Auto-Archive Summary:');
console.log(`  Total articles reviewed: ${archiveSummary.total_reviewed}`);
console.log(`  Articles to archive: ${archiveSummary.archived_count}`);
console.log(`  Articles kept: ${archiveSummary.kept_count}`);
console.log(`  Archive reasons: ${Object.entries(archiveSummary.archive_reasons).map(([r,c]) => `${r}(${c})`).join(', ')}`);
console.log(`  Avg scores - Archived: ${archiveSummary.avg_archived_score}, Kept: ${archiveSummary.avg_kept_score}`);

// Return data for next steps
return [{
  json: {
    archive_summary: archiveSummary,
    articles_to_archive: toArchive,
    articles_to_keep: toKeep,
    archive_timestamp: now.toISOString()
  }
}];

// Prepare archive updates for Notion
const archiveData = $input.item.json;
const articlesToArchive = archiveData.articles_to_archive;

if (articlesToArchive.length === 0) {
  console.log('No articles to archive this week');
  return [];
}

const archiveUpdates = articlesToArchive.map(article => ({
  json: {
    id: article.json.id,
    status: 'Archived',
    archive_reason: article.json.archive_reason,
    archived_at: article.json.archived_at,
    final_score: article.json.final_score
  }
}));

console.log(`Preparing to archive ${archiveUpdates.length} articles`);

return archiveUpdates;

// Extract kept articles for content selection
const archiveData = $('Auto-Archive Engine').item.json;
const keptArticles = archiveData.articles_to_keep;

console.log(`Proceeding with ${keptArticles.length} articles for content selection`);

return keptArticles;

// Weekly Content Selection Engine
// Calculates updated scores with pillar balancing and trending boost
// Selects optimal content mix for upcoming week

const articles = $input.all();
const now = new Date();
const ONE_DAY = 24 * 60 * 60 * 1000;

// Content strategy targets from docs
const PILLAR_TARGETS = {
  'Technology Strategy': 0.30,
  'Leadership & Management': 0.25,
  'Execution & Delivery': 0.20,
  'Founder Lessons': 0.15,
  'Market & AI Trends': 0.10
};

// Weekly content needs
const WEEKLY_CONTENT_NEEDS = {
  blog: 1, // 2 per month = ~0.5 per week, but let's plan 1 ahead
  linkedin: 3,
  newsletter: 1
};

// Calculate current pillar distribution
const pillarCounts = {};
const totalArticles = articles.length;

articles.forEach(article => {
  const pillar = article.json.property_content_pillar || 'Unknown';
  pillarCounts[pillar] = (pillarCounts[pillar] || 0) + 1;
});

// Calculate pillar balance scores (lower = more needed)
const pillarBalanceScores = {};
Object.keys(PILLAR_TARGETS).forEach(pillar => {
  const currentRatio = (pillarCounts[pillar] || 0) / totalArticles;
  const targetRatio = PILLAR_TARGETS[pillar];
  // If we're under target, give boost (lower score = higher priority)
  pillarBalanceScores[pillar] = currentRatio / targetRatio;
});

// Enhanced scoring function
function calculateWeeklyScore(article) {
  const json = article.json;

  // Base scores
  const relevance = json.property_relevance_score || 5;
  const actionability = json.property_actionability_score || 5;
  const depth = json.property_depth_score || 5;
  const priority = json.property_priority || 'Medium';

  // Base calculation: (Relevance × 3) + (Actionability × 2) + (Depth × 1)
  let baseScore = (relevance * 3) + (actionability * 2) + depth;

  // Priority bonus
  const priorityBonus = {
    'High': 4,
    'Medium': 2,
    'Low': 0
  }[priority] || 2;

  baseScore += priorityBonus;

  // Pillar balance modifier
  const pillar = json.property_content_pillar || 'Unknown';
  const balanceMultiplier = pillarBalanceScores[pillar] || 1;
  // If pillar is underrepresented (balance < 1), boost score
  const pillarBoost = balanceMultiplier < 1 ? (2 - balanceMultiplier) * 3 : 0;

  baseScore += pillarBoost;

  // Trending boost (articles from last 7 days get bonus)
  const createdDate = new Date(json.property_created_date || json.created_date);
  const daysSinceCreated = (now - createdDate) / ONE_DAY;

  let trendingBoost = 0;
  if (daysSinceCreated <= 3) trendingBoost = 3; // Very recent
  else if (daysSinceCreated <= 7) trendingBoost = 2; // Recent
  else if (daysSinceCreated <= 14) trendingBoost = 1; // Somewhat recent

  baseScore += trendingBoost;

  // Research complexity consideration
  // Higher depth scores might need more research time - slight penalty for immediate use
  const complexityPenalty = depth >= 8 ? 1 : 0;
  baseScore -= complexityPenalty;

  return Math.round(baseScore * 10) / 10; // Round to 1 decimal
}

// Calculate updated scores for all articles
const scoredArticles = articles.map(article => {
  const weeklyScore = calculateWeeklyScore(article);

  return {
    ...article,
    json: {
      ...article.json,
      weekly_score: weeklyScore,
      pillar_balance_score: pillarBalanceScores[article.json.property_content_pillar] || 1,
      trending_days: Math.round((now - new Date(article.json.property_created_date || article.json.created_date)) / ONE_DAY)
    }
  };
});

// Sort by weekly score (highest first)
const sortedArticles = scoredArticles.sort((a, b) => b.json.weekly_score - a.json.weekly_score);

// Select content for the week
const selections = {
  blog_candidates: [],
  linkedin_candidates: [],
  should_process: []
};

// Blog content selection (high-quality, research-heavy)
const blogCandidates = sortedArticles.filter(a => 
  a.json.weekly_score >= 22 && 
  (a.json.property_depth_score || 5) >= 6
).slice(0, 3); // Top 3 for choice

selections.blog_candidates = blogCandidates;

// LinkedIn content selection (actionable, engaging)
const linkedinCandidates = sortedArticles.filter(a => 
  a.json.weekly_score >= 18 && 
  (a.json.property_actionability_score || 5) >= 6
).slice(0, 6); // Top 6 for choice

selections.linkedin_candidates = linkedinCandidates;

// Auto-select "Should Process" - top 1 blog + top 3 LinkedIn (avoiding duplicates)
const autoSelected = new Set();

// Add top blog candidate
if (blogCandidates.length > 0) {
  selections.should_process.push(blogCandidates[0]);
  autoSelected.add(blogCandidates[0].json.id);
}

// Add top LinkedIn candidates (skip if already selected for blog)
let linkedinCount = 0;
for (const candidate of linkedinCandidates) {
  if (!autoSelected.has(candidate.json.id) && linkedinCount < 3) {
    selections.should_process.push(candidate);
    autoSelected.add(candidate.json.id);
    linkedinCount++;
  }
}

// Summary statistics
const summary = {
  total_articles_reviewed: articles.length,
  blog_candidates_found: blogCandidates.length,
  linkedin_candidates_found: linkedinCandidates.length,
  auto_selected_count: selections.should_process.length,
  pillar_distribution: pillarCounts,
  pillar_balance_scores: pillarBalanceScores,
  top_score: sortedArticles.length > 0 ? sortedArticles[0].json.weekly_score : 0,
  selection_timestamp: now.toISOString()
};

console.log('📊 Weekly Content Selection Summary:');
console.log(`  Articles reviewed: ${summary.total_articles_reviewed}`);
console.log(`  Blog candidates: ${summary.blog_candidates_found}`);
console.log(`  LinkedIn candidates: ${summary.linkedin_candidates_found}`);
console.log(`  Auto-selected: ${summary.auto_selected_count}`);
console.log(`  Top score: ${summary.top_score}`);

return [{
  json: {
    selections,
    summary,
    sorted_articles: sortedArticles.slice(0, 10) // Top 10 for email
  }
}];

// Update "Should Process" for selected articles
const selectionData = $input.item.json;
const shouldProcessArticles = selectionData.selections.should_process;

const updates = shouldProcessArticles.map(article => ({
  json: {
    id: article.json.id,
    should_process: true,
    weekly_score: article.json.weekly_score,
    selection_reason: 'Auto-selected by weekly content selection',
    selected_at: new Date().toISOString()
  }
}));

console.log(`Marking ${updates.length} articles as "Should Process"`);

return updates;

// Generate email summary for manual review
const selectionData = $('Content Selection Engine').item.json;
const { selections, summary, sorted_articles } = selectionData;

// Build HTML email content
let emailBody = `<h2>📚 Weekly Content Selection - ${new Date().toLocaleDateString('en-GB', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric',
  timeZone: 'Europe/London'
})}</h2>\n\n`;

// Add scheduling note
emailBody += `<p><strong>📅 Optimal Publishing Schedule:</strong><br>\n`;
emailBody += `• Tuesday: Framework/template shares (peak B2B engagement)<br>\n`;
emailBody += `• Wednesday: Industry observations/hot takes<br>\n`;
emailBody += `• Thursday: Personal lessons/behind-the-scenes</p>\n\n`;

// Summary section
emailBody += `<h3>📊 Selection Summary</h3>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li><strong>Articles Reviewed:</strong> ${summary.total_articles_reviewed}</li>\n`;
emailBody += `  <li><strong>Blog Candidates:</strong> ${summary.blog_candidates_found}</li>\n`;
emailBody += `  <li><strong>LinkedIn Candidates:</strong> ${summary.linkedin_candidates_found}</li>\n`;
emailBody += `  <li><strong>Auto-Selected:</strong> ${summary.auto_selected_count}</li>\n`;
emailBody += `</ul>\n\n`;

// Pillar balance
emailBody += `<h3>🎯 Content Pillar Balance</h3>\n`;
emailBody += `<ul>\n`;
Object.entries(summary.pillar_distribution).forEach(([pillar, count]) => {
  const percentage = ((count / summary.total_articles_reviewed) * 100).toFixed(1);
  emailBody += `  <li><strong>${pillar}:</strong> ${count} articles (${percentage}%)</li>\n`;
});
emailBody += `</ul>\n\n`;

// Auto-selected articles
if (selections.should_process.length > 0) {
  emailBody += `<h3>✅ Auto-Selected for Processing</h3>\n`;
  emailBody += `<p>These articles have been automatically marked as "Should Process":</p>\n`;
  emailBody += `<ul>\n`;

  selections.should_process.forEach(article => {
    const title = article.json.property_title || 'Untitled';
    const pillar = article.json.property_content_pillar || 'Unknown';
    const priority = article.json.property_priority || 'Medium';
    const score = article.json.weekly_score || 0;

    emailBody += `  <li><strong>${title}</strong><br>\n`;
    emailBody += `      Pillar: ${pillar} | Priority: ${priority} | Score: ${score}<br>\n`;
    emailBody += `      <a href="${article.json.property_article_link}">Read Original</a></li>\n`;
  });

  emailBody += `</ul>\n\n`;
}

// Blog candidates
if (selections.blog_candidates.length > 0) {
  emailBody += `<h3>📝 Blog Article Candidates</h3>\n`;
  emailBody += `<p>Top candidates for long-form content (high depth + research value):</p>\n`;
  emailBody += `<ul>\n`;

  selections.blog_candidates.forEach((article, index) => {
    const title = article.json.property_title || 'Untitled';
    const pillar = article.json.property_content_pillar || 'Unknown';
    const scores = `R:${article.json.property_relevance_score||0} A:${article.json.property_actionability_score||0} D:${article.json.property_depth_score||0}`;
    const weeklyScore = article.json.weekly_score || 0;

    emailBody += `  <li><strong>${index + 1}. ${title}</strong><br>\n`;
    emailBody += `      ${pillar} | Scores: ${scores} | Weekly: ${weeklyScore}<br>\n`;
    emailBody += `      <a href="${article.json.property_article_link}">Read Original</a></li>\n`;
  });

  emailBody += `</ul>\n\n`;
}

// LinkedIn candidates
if (selections.linkedin_candidates.length > 0) {
  emailBody += `<h3>💼 LinkedIn Content Candidates</h3>\n`;
  emailBody += `<p>Top candidates for social content (high actionability + engagement potential):</p>\n`;
  emailBody += `<ul>\n`;

  selections.linkedin_candidates.slice(0, 5).forEach((article, index) => {
    const title = article.json.property_title || 'Untitled';
    const pillar = article.json.property_content_pillar || 'Unknown';
    const actionability = article.json.property_actionability_score || 0;
    const weeklyScore = article.json.weekly_score || 0;

    emailBody += `  <li><strong>${index + 1}. ${title}</strong><br>\n`;
    emailBody += `      ${pillar} | Actionability: ${actionability} | Weekly: ${weeklyScore}<br>\n`;
    emailBody += `      <a href="${article.json.property_article_link}">Read Original</a></li>\n`;
  });

  emailBody += `</ul>\n\n`;
}

// Footer
emailBody += `<hr>\n`;
emailBody += `<p><strong>Next Steps:</strong></p>\n`;
emailBody += `<ul>\n`;
emailBody += `  <li>Review auto-selected articles in <a href="https://www.notion.so/9d899a561818444f8de1d7bd72ee1528">Content Pipeline</a></li>\n`;
emailBody += `  <li>Manually adjust "Should Process" if needed</li>\n`;
emailBody += `  <li>Begin research and content creation</li>\n`;
emailBody += `</ul>\n`;
emailBody += `<p><small>Generated at ${new Date().toLocaleString('en-GB', { timeZone: 'Europe/London' })}</small></p>`;

return [{
  json: {
    subject: `📚 Weekly Content Selection - ${summary.auto_selected_count} Articles Ready`,
    body: emailBody,
    selections,
    summary
  }
}];
